// setup

	var path = require("path");

// module

var BuildPaths = function(app, site) {
			
	var paths = {};				
	var folders = app.folders;

	paths.themesBase = path.join(app.siteBase, folders.themes);
	paths.contentBase = path.join(app.siteBase, folders.content);
	paths.publicBase = path.join(app.siteBase, folders.public);

	paths.themes = "/" + folders.themes;
	paths.themes_rel = "/" + app.folders.themes;
	
	paths.homepage = path.join(paths.contentBase, site.homepage);	
	paths.error = path.join(paths.contentBase, site.error);
	
	return paths;
	
}

module.exports = BuildPaths;