// setup

	var path = require("path");

// module

var BuildPaths = function(app, site) {
			
	var paths = {};

	paths.themesBase = path.join(app.siteBase, app.folders.themes);
	paths.contentBase = path.join(app.siteBase, app.folders.content);
	paths.publicBase = path.join(app.siteBase, app.folders.public);

	paths.themes = "/" + app.folders.themes;	
	paths.homepage = path.join(paths.contentBase, site.homepage);	
	paths.error = path.join(paths.contentBase, site.error);
	
	return paths;
	
}

module.exports = BuildPaths;