// setup

	var _ = require("lodash");
	
	var peezyLogger = {};
	
	// define levels
	var levels = {
		"silent": 0,
		"concise": 1,
		"verbose": 2,
		"debug": 3
	};

	// set default
	peezyLogger.level = 2;

// methods
	
	// log msg
	peezyLogger.log = function(arg1, arg2) {
		
		if (is_level('concise')) {
		
			if (arg1) console.log(arg1);
			else console.log("hello world");
			
			// if arg2 is empty, do nothing
			if (arg2 === false) {
			}
			
			// else display arg2
			else if (arg2 && is_level('verbose')) {				
				console.log(arg2);
				console.log("");				
			}

			// else line break			
			else console.log("");
	
		}	
	
	}
	
	// inspect array
	peezyLogger.inspect = function(arr, msg) {
	
		// if concise, show summary
		if (is_level('concise')) this.log(_.size(arr) + " " + msg + ":", _.keys(arr));
		
		// if debug, show details
		if (is_level('debug')) this.log(arr);
	
	}

// helpers

	function is_level(level) {
		if (peezyLogger.level >= levels[level]) return true;
		else return false;
	}

// exports

module.exports = peezyLogger;