var _ = require("underscore");

var peezyLogger = {};

//define levels
var levels = {
	"silent": 0,
	"concise": 1,
	"verbose": 2,
	"debug": 3
};

// set default
peezyLogger.level = 2;

// log msg
peezyLogger.log = function(arg1, arg2) {
	
	if (is_level('concise')) {
	
		if (arg1) console.log(arg1);
		else console.log("hello world");
		
		if (arg2 === false) {
			// do nothing
		}
		
		else if (arg2 && is_level('verbose')) {
			
			console.log(arg2);
			console.log("");
			
		}
		
		else console.log("");

	}	

}

// inspect array
peezyLogger.inspect = function(arr, msg) {

	if (is_level('concise')) this.log(_.size(arr) + " " + msg + ":", _.keys(arr));
	
	if (is_level('debug')) this.log(arr);

}

// helpers
function is_level(level) {
	return true;
	if (this.level >= levels[level]) return true;
	else return false;
}

module.exports = peezyLogger;