var _ = require("underscore");

var logger = {};

logger.status = false;

logger.enable = function() {
	this.status = true;
}

logger.log = function(arg1, arg2) {
	
	if (this.status) {
	
		if (arg1) console.log(arg1);
		else console.log("hello world");
		
		if (arg2 === false) {
			// do nothing
		}
		
		else if (arg2) {
			
			console.log(arg2);
			console.log("");
			
		}
		
		else console.log("");

	}	

}

logger.inspect = function(arr, msg) {
	this.log(_.size(arr) + " " + msg + ":", _.keys(arr));
}	

module.exports = logger;