// setup

	var isValidEmail = require("is-valid-email");
	
	var util = {};

// module

	// validate email

	util.isEmail = function (email) {	
		return isValidEmail(email);	
	}
	
	// remove spaces from filename
	
	util.removeSpaces = function(str) {
		str.replace(/\s+/g, '');
	}
			

// export

module.exports = util;